  return <>
      <Wrap gap="md">
        <Calendar dayProps={{
        color: "blue.500",
        _weekend: {
          color: "green.500"
        },
        _outside: {
          color: "orange.500"
        }
      }} />

        <Calendar dayProps={{
        sx: {
          _outside: {
            pointerEvents: "none"
          }
        },
        component: ({
          isOutside,
          date
        }) => !isOutside ? <Text as="span">{date.getDate()}</Text> : null
      }} />

        <Calendar dayProps={{
        component: ({
          col,
          row,
          date
        }) => <Text as="span" color={col === 3 || row === 3 ? "blue.500" : undefined}>
                {date.getDate()}
              </Text>
      }} />
      </Wrap>

      <Wrap gap="md">
        <Calendar dayProps={{
        component: ({
          date
        }) => <Tooltip label="No schedule">
                <Center as="span" w="full" h="full">
                  {date.getDate()}
                </Center>
              </Tooltip>
      }} />

        <Calendar dateFormat="YYYY年 MMMM" locale="ja" colorScheme="primary" dayProps={{
        component: ({
          date
        }) => <Popover>
                <PopoverTrigger>
                  <Center as="span" w="full" h="full">
                    {date.getDate()}
                  </Center>
                </PopoverTrigger>

                <Portal>
                  <PopoverContent>
                    <PopoverHeader>
                      {date.getMonth() + 1}月{date.getDate()}日
                    </PopoverHeader>
                    <PopoverBody>
                      <VStack minW="sm" gap="sm">
                        <Card variant="solid" colorScheme="secondary" size="sm" fontSize="sm">
                          <CardBody>山田の誕生日</CardBody>
                        </Card>
                      </VStack>
                    </PopoverBody>
                  </PopoverContent>
                </Portal>
              </Popover>
      }} />
      </Wrap>

      <Calendar dateFormat="YYYY年 MMMM" locale="ja" size="full" type="date" headerProps={{
      mb: 2
    }} labelProps={{
      pointerEvents: "none",
      icon: {
        display: "none"
      }
    }} tableProps={{
      border: "1px solid",
      borderColor: "border",
      th: {
        border: "1px solid",
        borderColor: "border"
      },
      td: {
        border: "1px solid",
        borderColor: "border"
      }
    }} dayProps={{
      h: "auto",
      p: 2,
      _selected: undefined,
      _hover: undefined,
      _active: undefined,
      transitionProperty: "none",
      component: ({
        date,
        isSelected
      }) => <VStack alignItems="center">
              <Center bg={isSelected ? "primary" : undefined} w={8} lineHeight={8} rounded="full" color={isSelected ? "white" : undefined} transitionProperty="background" transitionDuration="normal">
                {date.getDate()}
              </Center>

              <VStack gap="sm">
                <Card variant="solid" colorScheme="secondary" size="sm" fontSize="sm">
                  <CardBody>
                    <Text>山田の誕生日</Text>
                  </CardBody>
                </Card>
              </VStack>
            </VStack>
    }} />
    </>;
